#!/bin/bash
# Redis Initialization Script
# Sets up security keys required by ggo-cuentas microservice

echo "Setting up Redis security keys..."

# Wait for Redis to be ready
sleep 5

# Set public security key
redis-cli -h my-     SET "security_key:test:public" ""

# Set private security key
redis-cli -h my-redis SET "security_key:test:private" ""

echo "Redis security keys configured successfully!"
